import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { requireAuth, requireAdmin } from "./localAuth";
import { sql } from "drizzle-orm";
import { insertStreamSchema, insertGiftSchema, insertChatMessageSchema, users, streams, memoryFragments, memoryInteractions, insertMemoryFragmentSchema, insertMemoryInteractionSchema, registerSchema, loginSchema, insertCommentSchema, insertCommentLikeSchema, comments, commentLikes, chatMessages, giftCharacters, gifts, notifications, insertNotificationSchema, messages, blockedUsers, followers } from "@shared/schema";
import { z } from "zod";
import { eq, and, desc, ne } from "drizzle-orm";
import { db } from "./db";
import bcrypt from "bcryptjs";
import passport from "passport";
// @ts-ignore
import { checkSuperAdmin } from "./middleware/checkSuperAdmin.js";
import { trackUserActivity, cleanupStaleOnlineUsers } from "./middleware/activityTracker";
import { filterOwnerFromUsers, logOwnerProtection, isOwnerUsername, isOwnerEmail, isOwnerAccount } from './owner-protection';
import path from 'path';
import { fileURLToPath } from 'url';
import { dirname } from 'path';
import multer from 'multer';
import fs from 'fs/promises';
import { setupDirectMessageRoutes } from './routes/direct-messages';
import { setupSimpleMessageRoutes } from './routes/simple-messages';
import { setupPrivateRoomRoutes } from './routes/private-rooms';
import { setupGroupRoomRoutes } from './routes/group-rooms';
import { setupWalletRoutes } from './routes/wallet';
import { registerStripeRoutes } from './routes/stripe';
import { setupFollowRoutes } from './routes/follow';
import { updateSupporterLevel, updateGiftsReceived } from './supporter-system';
import { initializePointPackages } from './init-point-packages';
import crypto from 'crypto';
import axios from 'axios';
import { UrlHandler } from './utils/url-handler';
import cors from 'cors';
import { BackblazeService } from './services/backblaze-service';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Constants for media serving
const BUCKET_NAME = 'replit-objstore-b9b8cbbd-6b8d-4fcb-b924-c5e56e084f16'; // Replit's default bucket name
const FALLBACK_MEDIA_DIR = path.join(__dirname, 'public', 'media'); // Local directory for media files

// Initialize Backblaze Service
const backblazeService = new BackblazeService();

// Security functions for ZegoCloud protection
const secureTokens = new Map<string, { token: string; expires: number; userId: string }>();

// Notification helper function
async function createNotification(data: {
  userId: string;
  fromUserId: string;
  type: 'comment' | 'like' | 'gift' | 'share' | 'follow' | 'message';
  title: string;
  message: string;
  relatedId?: number;
  relatedType?: string;
}) {
  try {
    await db.insert(notifications).values({
      userId: data.userId,
      fromUserId: data.fromUserId,
      type: data.type,
      title: data.title,
      message: data.message,
      relatedId: data.relatedId,
      relatedType: data.relatedType,
      isRead: false
    });
  } catch (error) {
    console.error("Error creating notification:", error);
  }
}

function generateSecureToken(userId: string): string {
  // Generate a secure temporary token
  const token = crypto.randomBytes(32).toString('hex');
  const expires = Date.now() + (30 * 60 * 1000); // 30 minutes expiry

  // Clean expired tokens using Array.from to fix iterator issue
  const expiredTokens: string[] = [];
  for (const [key, value] of Array.from(secureTokens.entries())) {
    if (value.expires < Date.now()) {
      expiredTokens.push(key);
    }
  }
  expiredTokens.forEach(key => secureTokens.delete(key));

  // Store new token
  secureTokens.set(token, { token, expires, userId });

  return token;
}

function validateSecureToken(token: string, userId: string): boolean {
  const tokenData = secureTokens.get(token);

  if (!tokenData) return false;
  if (tokenData.expires < Date.now()) {
    secureTokens.delete(token);
    return false;
  }
  if (tokenData.userId !== userId) return false;

  return true;
}

// Encrypted ZegoCloud configuration - never expose raw secrets
function getSecureZegoConfig() {
  const appId = process.env.ZEGO_APP_ID;
  const appSign = process.env.ZEGO_APP_SIGN;
  const serverSecret = process.env.ZEGO_SERVER_SECRET;

  if (!appId || !appSign || !serverSecret) {
    console.error('Missing ZegoCloud credentials:', {
      appId: !!appId,
      appSign: !!appSign,
      serverSecret: !!serverSecret
    });
    throw new Error('ZegoCloud credentials not configured');
  }

  console.log('ğŸ”’ ZegoCloud configuration loaded successfully');

  // Only return app ID, all secrets stay on server
  return {
    appId: appId,
    // Generate hash for validation without exposing secret
    configHash: crypto.createHash('sha256').update(serverSecret + appId + appSign).digest('hex').substring(0, 16)
  };
}

// Clean expired tokens periodically - using Array.from to avoid iterator issues
setInterval(() => {
  const now = Date.now();
  let cleanedCount = 0;
  const expiredTokens: string[] = [];

  // Convert to array first to avoid iterator issues
  for (const [token, tokenData] of Array.from(secureTokens.entries())) {
    if (tokenData.expires < now) {
      expiredTokens.push(token);
      cleanedCount++;
    }
  }

  // Delete expired tokens
  expiredTokens.forEach(token => secureTokens.delete(token));

  if (cleanedCount > 0) {
    console.log(`ğŸ§¹ Cleaned ${cleanedCount} expired security tokens`);
  }
}, 10 * 60 * 1000); // Every 10 minutes

// Fix iterator issue by using Array.from for Map entries
function cleanupUserTokens(userId: string): number {
  let cleanedCount = 0;
  const tokensToDelete: string[] = [];

  // Convert Map entries to array to avoid iterator issues
  for (const [token, tokenData] of Array.from(secureTokens.entries())) {
    if (tokenData.userId === userId) {
      tokensToDelete.push(token);
      cleanedCount++;
    }
  }

  // Delete the tokens
  tokensToDelete.forEach(token => secureTokens.delete(token));

  if (cleanedCount > 0) {
    console.log(`ğŸ§¹ Cleaned ${cleanedCount} security tokens for user ${userId}`);
  }

  return cleanedCount;
}

// Import Object Storage utilities
import { uploadFileToStorage, generateUniqueFileName, deleteFileFromStorage } from './object-storage';
import { Storage } from '@google-cloud/storage';
import { UrlHandler } from './utils/url-handler';

// Using Backblaze B2 Cloud Storage as primary storage system
console.log('ğŸ”§ Using Backblaze B2 Cloud Storage as primary storage system');
console.log(`ğŸ“¡ Backblaze B2 Available: ${backblazeService.isAvailable()}`);

if (backblazeService.isAvailable()) {
  console.log('âœ… Backblaze B2 configured and ready');
} else {
  console.log('âš ï¸ Backblaze B2 not configured - check environment variables');
  console.log('ğŸ“‹ Required: B2_APPLICATION_KEY_ID, B2_APPLICATION_KEY, B2_BUCKET_NAME, B2_BUCKET_ID');
}

// Configure multer for file uploads using memory storage for Object Storage
const upload = multer({
  storage: multer.memoryStorage(), // Use memory storage for Object Storage
  limits: {
    fileSize: 50 * 1024 * 1024, // 50MB limit
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp', 'video/mp4', 'video/webm', 'video/quicktime'];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type. Only images and videos are allowed.'));
    }
  },
});

interface ConnectedClient {
  ws: WebSocket;
  userId?: string;
  streamId?: number;
}

const connectedClients = new Map<string, ConnectedClient>();

export async function registerRoutes(app: Express): Promise<Server> {
  // Initialize gift characters
  await initializeGiftCharacters();

  // Initialize point packages
  await initializePointPackages();

  // Setup activity tracking for all authenticated routes
  app.use('/api', trackUserActivity);

  // Setup periodic cleanup of stale online users (every 2 minutes)
  setInterval(cleanupStaleOnlineUsers, 2 * 60 * 1000);

  // Setup message routes
  setupDirectMessageRoutes(app);

  // Setup private room routes
  setupPrivateRoomRoutes(app);

  // Setup group room routes
  setupGroupRoomRoutes(app);

  // Setup wallet routes
  setupWalletRoutes(app);

  // Setup follow routes
  setupFollowRoutes(app);

  // Setup Stripe payment routes
  registerStripeRoutes(app);

  // Enhanced CORS configuration
  app.use(cors({
    origin: ["http://localhost:3000", "http://localhost:5000"],
    credentials: true,
  }));

  // URL normalization middleware for media URLs
  app.use((req, res, next) => {
    // Normalize old media URLs to use unified endpoint
    if (req.path.startsWith('/public-objects/') || req.path.startsWith('/media/')) {
      const filename = req.path.split('/').pop();
      return res.redirect(301, `/api/media/${filename}`);
    }
    next();
  });

  // Simple in-memory cache for B2 files
  const mediaCache = new Map();
  const cacheTimeout = 15 * 60 * 1000; // 15 minutes

  // Dedicated Backblaze B2 media endpoint - optimized with caching
  app.get('/api/media/b2/:filename', async (req, res) => {
    try {
      const { filename } = req.params;

      // Check cache first
      const cached = mediaCache.get(filename);
      if (cached && Date.now() - cached.timestamp < cacheTimeout) {
        console.log('âš¡ Cache hit for:', filename);
        res.set(cached.headers);
        return res.send(cached.buffer);
      }

      console.log('ğŸ–¼ï¸ Fetching B2 image:', filename);

      if (!backblazeService.isAvailable()) {
        return res.status(503).json({ error: 'Backblaze B2 not available' });
      }

      // Get the public URL directly (no authorization needed for public bucket)
      const publicUrl = await backblazeService.getFileUrl(filename);
      console.log('ğŸ”— Using public URL for:', filename);

      // Fetch the file from B2
      const response = await fetch(publicUrl);

      if (!response.ok) {
        console.error('âŒ Failed to fetch from B2:', response.status, response.statusText);
        // If 401/403, the file might be in a private location, try with auth
        if (response.status === 401 || response.status === 403) {
