  // Memory Fragment operations
  async createMemoryFragment(fragmentData: InsertMemoryFragment): Promise<MemoryFragment> {
    // Calculate expiration based on memory type
    let expiresAt: Date | null = null;

    if (fragmentData.memoryType !== 'permanent') {
      expiresAt = new Date();
      switch (fragmentData.memoryType) {
        case 'flash':
        case 'fleeting':
          expiresAt.setHours(expiresAt.getHours() + 3); // 3 hours as shown in UI
          break;
        case 'trending':
        case 'precious':
          expiresAt.setHours(expiresAt.getHours() + 12); // 12 hours as shown in UI
          break;
        case 'star':
          expiresAt.setHours(expiresAt.getHours() + 24); // 24 hours as shown in UI
          break;
        case 'legend':
        case 'legendary':
          expiresAt.setDate(expiresAt.getDate() + 7); // 1 week as shown in UI
          break;
        default:
          expiresAt.setHours(expiresAt.getHours() + 24);
      }
    }
    // For 'permanent' type, expiresAt remains null (never expires)

    const [fragment] = await db
      .insert(memoryFragments)
      .values({
        ...fragmentData,
        expiresAt,
      })
      .returning();
    return fragment;
  }

