  animation: rabbit-head-shake 3s ease-in-out infinite, rabbit-blink 4s ease-in-out infinite;
  transform-origin: center center;
}

/* TikTok-inspired animations and styles */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* TikTok-style streaming interface */
.streaming-interface {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

.streaming-chat {
  background: linear-gradient(to top, rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.3));
  backdrop-filter: blur(10px);
}

.gift-panel {
  background: rgba(0, 0, 0, 0.9);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.live-indicator {
  animation: pulse-shadow 2s infinite;
}

@keyframes pulse-shadow {
  0% { box-shadow: 0 0 0 0 rgba(220, 38, 38, 0.7); }
  70% { box-shadow: 0 0 0 10px rgba(220, 38, 38, 0); }
  100% { box-shadow: 0 0 0 0 rgba(220, 38, 38, 0); }
}

.tiktok-button {
  transition: all 0.3s ease;
  transform: scale(1);
}

.tiktok-button:active {
  transform: scale(0.95);
}

.gift-animation {
  animation: float-up 3s ease-out forwards;
}

@keyframes float-up {
  0% {
    transform: translateY(100px) scale(0);
    opacity: 0;
  }
  20% {
    transform: translateY(0) scale(1.2);
    opacity: 1;
  }
  100% {
    transform: translateY(-300px) scale(0.8);
    opacity: 0;
  }
}

/* Image/Video Loading Optimizations */
img, video {
  will-change: transform;
  backface-visibility: hidden;
  perspective: 1000;
  transform: translate3d(0, 0, 0);
}

img {
  image-rendering: -webkit-optimize-contrast;
  image-rendering: crisp-edges;
}

/* Lazy loading skeleton */
.loading-skeleton {
  background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
  background-size: 200% 100%;
  animation: loading-wave 1.5s infinite;
}

@keyframes loading-wave {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

/* Mobile-optimized video controls */
@media (max-width: 768px) {
  .video-controls button {
    min-height: 44px;
    min-width: 44px;
  }

  .video-overlay {
    padding: 12px;
  }

  .video-sidebar {
    right: 8px;
  }
}

/* Pulse animation variations */
@keyframes pulse-slow {
  0%, 100% {
    opacity: 0.3;
  }
  50% {
    opacity: 0.6;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

.animate-pulse-slow {
  animation: pulse-slow 3s ease-in-out infinite;
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

/* TikTok-style gradient text */
.gradient-text {
  background: linear-gradient(45deg, #ff006e, #fb5607, #ffbe0b);
  background-size: 400% 400%;
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradient-shift 3s ease infinite;
}

@keyframes gradient-shift {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

/* Glassmorphism effects */
.glass-effect {
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* TikTok-style button hover effects */
.tiktok-button {
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
}

.tiktok-button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.tiktok-button:hover::before {
  left: 100%;
}

/* Custom toggle switch */
.toggle-switch {
  position: relative;
  display: inline-block;
  width: 48px;
  height: 24px;
}

.toggle-switch input {
  opacity: 0;
  width: 0;
  height: 0;
}

.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #6b7280;
  transition: 0.4s;
  border-radius: 24px;
}

.slider:before {
  position: absolute;
  content: "";
  height: 20px;
  width: 20px;
  left: 2px;
  bottom: 2px;
  background-color: white;
  transition: 0.4s;
  border-radius: 50%;
}

input:checked + .slider {
  background: linear-gradient(45deg, #ec4899, #8b5cf6);
}

input:checked + .slider:before {
  transform: translateX(24px);
}

/* Fix for Arabic text in flipped cards */
.flip-card-back {
  transform: rotateY(180deg);
  direction: rtl;
  text-align: right;
}

.flip-card-back * {
  direction: rtl;
  text-align: right;
}

/* Mobile-first responsive design */
@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  /* Mobile viewport handling */
  html {
    -webkit-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    touch-action: manipulation;
  }

  /* Better touch targets for mobile */
  button, [role="button"], input[type="submit"], input[type="reset"], input[type="button"] {
    @apply min-h-[44px] min-w-[44px];
    -webkit-tap-highlight-color: rgba(0,0,0,0);
  }

  /* Mobile scroll optimization */
  .scroll-smooth {
    -webkit-overflow-scrolling: touch;
  }

  /* RTL support improvements */
  [dir="rtl"] {
    text-align: right;
  }

  [dir="rtl"] .rtl\:space-x-reverse > :not([hidden]) ~ :not([hidden]) {
    --tw-space-x-reverse: 1;
  }
}

/* Dark mode support */
:root {
  --background: 0 0% 100%;
  --foreground: 222.2 84% 4.9%;
  --card: 0 0% 100%;
  --card-foreground: 222.2 84% 4.9%;

  /* LaaBoBo Custom Colors */
