    // Ù…Ø­Ø§ÙˆÙ„Ø© Ø§Ø³ØªØ®Ø±Ø§Ø¬ Ø§Ù„Ø±Ø³Ø§Ù„Ø© Ù…Ù† JSON response
    try {
      const parsed = JSON.parse(text);
      if (parsed.message) {
