    viewCount: number;
    likeCount: number;
    shareCount: number;
    giftCount: number;
    visibilityLevel: 'public' | 'followers' | 'private';
    allowComments: boolean;
    allowSharing: boolean;
    allowGifts: boolean;
    expiresAt: string;
    createdAt: string;
    author: {
      id: string;
      username: string;
      profileImageUrl?: string;
      firstName?: string;
      isVerified?: boolean;
      verificationBadge?: string;
