  };
  isOwner?: boolean;
  onLike?: () => void;
  onComment?: () => void;
  onShare?: () => void;
  onSendGift?: () => void;
  onEdit?: () => void;
  onDelete?: () => void;
}

export default function MemoryCard({
  memory,
  isOwner = false,
  onLike,
  onComment,
  onShare,
  onSendGift,
  onEdit,
  onDelete
}: MemoryCardProps) {
  const [isLiked, setIsLiked] = useState(false);
  const [showImageViewer, setShowImageViewer] = useState(false);

  const getMemoryIcon = (type: string) => {
    switch (type) {
      case 'fleeting': return 'ğŸ’«';
      case 'precious': return 'ğŸ’';
      case 'legendary': return 'ğŸ‘‘';
      default: return 'âœ¨';
    }
  };

  const getMemoryColor = (type: string) => {
    switch (type) {
      case 'fleeting': return 'from-blue-400 to-cyan-400';
      case 'precious': return 'from-purple-400 to-pink-400';
      case 'legendary': return 'from-yellow-400 to-orange-400';
      default: return 'from-gray-400 to-gray-500';
