                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                {isOwner ? (
                  <>
                    <DropdownMenuItem onClick={onEdit}>
                      <Settings className="w-4 h-4 mr-2" />
                      ØªØ¹Ø¯ÙŠÙ„ Ø§Ù„Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem 
                      onClick={onDelete}
                      className="text-red-600"
                    >
                      Ø­Ø°Ù Ø§Ù„Ø°ÙƒØ±Ù‰
                    </DropdownMenuItem>
                  </>
                ) : (
                  <>
                    <DropdownMenuItem>
                      <Eye className="w-4 h-4 mr-2" />
                      Ø¹Ø±Ø¶ ÙƒØ§Ù…Ù„
                    </DropdownMenuItem>
                    <DropdownMenuItem>
                      Ø¥Ø¨Ù„Ø§Øº Ø¹Ù† Ù…Ø­ØªÙˆÙ‰
                    </DropdownMenuItem>
                  </>
                )}
              </DropdownMenuContent>
            </DropdownMenu>
          </div>

          {/* Energy Bar */}
          <div className="mt-3">
            <div className="flex items-center justify-between text-xs text-gray-600 mb-1">
              <span>Ø·Ø§Ù‚Ø© Ø§Ù„Ø°ÙƒØ±Ù‰</span>
              <span>{memory.currentEnergy}/{memory.initialEnergy}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className={`h-2 rounded-full bg-gradient-to-r ${getMemoryColor(memory.memoryType)}`}
                style={{ width: `${energyPercentage}%` }}
              />
            </div>
          </div>
        </div>

        {/* Media Content */}
        <CardContent className="p-0">
          <div className="relative">
            {memory.type === 'image' && memory.thumbnailUrl ? (
              <div 
                className="relative cursor-pointer"
                onClick={() => setShowImageViewer(true)}
              >
                <img
                  src={memory.thumbnailUrl}
                  alt={memory.title || 'Memory'}
                  className="w-full h-64 object-cover cursor-pointer hover:opacity-95 transition-opacity"
                  loading="eager"
                  decoding="async"
                />
              </div>
            ) : memory.type === 'video' && memory.mediaUrls?.[0] ? (
              <div 
                className="relative cursor-pointer"
                onClick={() => {
                  // ÙØªØ­ Ø§Ù„ÙÙŠØ¯ÙŠÙˆ ÙÙŠ ØµÙØ­Ø© ÙƒØ§Ù…Ù„Ø© Ù…Ø«Ù„ TikTok Ø¨Ù†ÙØ³ Ø£Ø³Ù„ÙˆØ¨ Ø§Ù„ØµÙØ­Ø© Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠØ©
                  window.location.href = `/video/${memory.id}`;
                }}
              >
                <video
                  src={memory.mediaUrls[0]}
                  className="w-full h-64 object-cover"
                  muted
                  loop
                  playsInline
                  preload="auto"
                  onMouseEnter={(e) => e.currentTarget.play()}
                  onMouseLeave={(e) => e.currentTarget.pause()}
                  onCanPlay={(e) => {
                    e.currentTarget.currentTime = 0.01;
                  }}
                />
                {/* Play button overlay */}
                <div className="absolute inset-0 flex items-center justify-center bg-black/20 hover:bg-black/30 transition-colors">
                  <div className="w-16 h-16 bg-white/90 rounded-full flex items-center justify-center">
                    <div className="w-0 h-0 border-l-6 border-l-gray-800 border-t-4 border-t-transparent border-b-4 border-b-transparent ml-1"></div>
                  </div>
                </div>
              </div>
            ) : (
              <div 
                className={`w-full h-64 bg-gradient-to-br ${getMemoryColor(memory.memoryType)} flex items-center justify-center text-6xl cursor-pointer hover:opacity-95 transition-opacity`}
                onClick={onComment}
              >
                {getMemoryIcon(memory.memoryType)}
              </div>
            )}

            {/* Overlay with memory type */}
            <div className="absolute top-2 left-2">
              <Badge className={`bg-black/70 text-white`}>
                {memory.type === 'video' ? 'ğŸ“¹' : 'ğŸ“·'} {memory.type === 'video' ? 'ÙÙŠØ¯ÙŠÙˆ' : 'ØµÙˆØ±Ø©'}
              </Badge>
            </div>
          </div>

          {/* Caption */}
          {memory.caption && (
            <div className="p-4 pb-2">
              <p className="text-sm text-gray-700">{memory.caption}</p>
            </div>
          )}

          {/* Actions */}
          <div className="p-4 pt-2">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4 rtl:space-x-reverse">
                <Button
                  variant="ghost"
