import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useLanguage } from "@/contexts/LanguageContext";
import { useQuery } from "@tanstack/react-query";

import { 
  Home, 
  Radio,
  Plus,
  MessageCircle,
  User,
  LogOut,
  Wallet,
  Gift,
  Video,
  Grid3X3
} from "lucide-react";

export default function BottomNavigation() {
  const { user } = useAuth();
  const { t } = useLanguage();
  const [location] = useLocation();


  // Fetch conversations to get unread count
  const { data: conversations = [] } = useQuery({
    queryKey: ['/api/messages/conversations'],
    queryFn: async () => {
      const response = await fetch('/api/messages/conversations', {
        credentials: 'include'
      });
      if (!response.ok) throw new Error('Failed to fetch conversations');
      return response.json();
    },
    enabled: !!user,
    refetchInterval: 3000, // Update every 3 seconds
    staleTime: 1000 // Data becomes stale after one second
  });

  // Calculate total unread messages
  const totalUnreadCount = conversations.reduce((total: number, conv: any) => 
    total + (conv.unreadCount || 0), 0
  );

  const navItems = [
    { href: "/videos", icon: Home, label: "Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠØ©" },
    { href: "/home", icon: Grid3X3, label: "Ù…Ù†Ø´ÙˆØ±Ø§Øª" },
    { href: "/gifts", icon: Gift, label: t('nav.gifts') },
    { href: "/create-memory", icon: Plus, label: t('common.create'), isSpecial: true },
    { href: "/messages", icon: MessageCircle, label: t('nav.messages') },
    { href: "/explore", icon: Radio, label: t('stream.start_streaming') },
    { href: "/profile", icon: User, label: t('nav.profile') }
  ];

  return (
    <nav className="fixed bottom-0 left-0 right-0 bg-white/95 backdrop-blur-md border-t border-gray-200/50 z-50 md:hidden shadow-2xl">
      <div className="relative flex items-center justify-around py-1 px-0" style={{ height: '56px' }}>
        {navItems.map((item, index) => {
          const isActive = location === item.href;
          const Icon = item.icon;

          if (item.isSpecial) {
            return (
              <Link key={item.href} href={item.href}>
                <div className="flex flex-col items-center justify-center -mt-4 relative">
                  <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-600 rounded-full flex items-center justify-center shadow-xl hover:shadow-2xl transform hover:scale-105 transition-all duration-200 border-2 border-white">
                    <Icon className="w-6 h-6 text-white" />
                  </div>
                  <span className="text-[9px] mt-0.5 text-purple-600 font-medium text-center">{item.label}</span>
                </div>
              </Link>
            );
          }

          if ('isLogout' in item && item.isLogout) {
            return (
              <button 
                key={index}
                onClick={async () => {
                  try {
                    const response = await fetch("/api/logout", {
                      method: "POST",
                      credentials: "include",
                    });
                    window.location.replace("/login");
                  } catch (error) {
                    console.error("Logout error:", error);
                    window.location.replace("/login");
                  }
                }}
                className="flex flex-col items-center justify-center py-2 px-3 rounded-xl transition-all duration-200 text-red-500 hover:text-red-600 hover:bg-red-50"
              >
                <Icon className="w-6 h-6" />
                <span className="text-xs mt-1 font-medium">{item.label}</span>
              </button>
            );
          }

          return (
            <Link key={item.href} href={item.href}>
              <div className={`flex flex-col items-center justify-center py-2 px-2 rounded-xl transition-all duration-200 relative ${
                isActive 
                  ? 'text-purple-600 bg-purple-50 scale-105' 
                  : 'text-gray-500 hover:text-purple-400 hover:bg-gray-50'
              }`}>
                <div className="relative">
                  <Icon className={`w-5 h-5 ${isActive ? 'animate-pulse' : ''}`} />
                  {item.href === '/messages' && totalUnreadCount > 0 && (
                    <span className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 text-white text-xs rounded-full flex items-center justify-center" style={{ fontSize: '8px' }}>
                      {totalUnreadCount > 99 ? '99+' : totalUnreadCount}
                    </span>
                  )}
                </div>
                <span className={`text-[9px] mt-0.5 font-medium ${isActive ? 'text-purple-600' : ''}`}>{item.label}</span>
              </div>
            </Link>
          );
        })}
      </div>
    </nav>
  );
}

