export function optimizeNavigation() {
  // Ù…Ù†Ø¹ Ø¥Ø¹Ø§Ø¯Ø© Ø§Ù„ØªØ­Ù…ÙŠÙ„ ØºÙŠØ± Ø§Ù„Ø¶Ø±ÙˆØ±ÙŠØ©
  const links = document.querySelectorAll('a[href^="/"]');

  links.forEach(link => {
    link.addEventListener('click', (e) => {
      e.preventDefault();
