        .where(eq(userWallets.userId, userId))
        .limit(1);

      // Create wallet if doesn't exist
      if (!wallet.length) {
        const newWallet = await db
          .insert(userWallets)
          .values({
            userId,
            totalEarnings: 0,
            availableBalance: 0,
            totalWithdrawn: 0
          })
          .returning();

        wallet = newWallet;
      }

      res.json(wallet[0]);

    } catch (error) {
      console.error("âŒ Error fetching wallet:", error);
      res.status(500).json({ message: "Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø­ÙØ¸Ø©" });
    }
  });

  // Get wallet transactions
  app.get('/api/wallet/transactions', requireAuth, async (req: any, res) => {
    try {
      const userId = req.user.id;

      const transactions = await db
        .select()
        .from(walletTransactions)
        .where(eq(walletTransactions.userId, userId))
        .orderBy(desc(walletTransactions.createdAt))
        .limit(50);

      res.json(transactions);

    } catch (error) {
      console.error("âŒ Error fetching wallet transactions:", error);
      res.status(500).json({ message: "Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø³Ø¬Ù„ Ø§Ù„Ù…Ø¹Ø§Ù…Ù„Ø§Øª" });
    }
  });

  // Withdraw earnings to points
  app.post('/api/wallet/withdraw', requireAuth, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const { amount } = req.body;

      if (!amount || amount <= 0) {
        return res.status(400).json({ message: "Ù…Ø¨Ù„Øº ØºÙŠØ± ØµØ­ÙŠØ­ Ù„Ù„Ø³Ø­Ø¨" });
      }

      // Get current wallet
      const wallet = await db
        .select()
