            <div>Ø§Ù„ÙÙŠØ¯ÙŠÙˆ ØºÙŠØ± Ù…ØªÙˆÙØ±</div>
          </div>
        </div>
      )}
      <video
        ref={videoRef}
        src={currentSrc}
        className={`${className || ''} ${isLoading ? 'opacity-0' : 'opacity-100'} transition-opacity duration-300`}
        muted={muted}
