  // Ù…Ø¹Ø§Ù„Ø¬Ø© Ù…ØµÙÙˆÙØ© Ù…Ù† Ø§Ù„Ø±ÙˆØ§Ø¨Ø·
  static processMediaUrls(mediaUrls: string[], req: Request): string[] {
