  }

  // Ø¥Ù†Ø´Ø§Ø¡ Ø±Ø§Ø¨Ø· API Ù„Ù„ÙˆØ³Ø§Ø¦Ø·
  static createApiMediaUrl(filename: string, req: Request): string {
