  // ØªØ­ÙˆÙŠÙ„ Ø§Ù„Ø±Ø§Ø¨Ø· Ø§Ù„Ù‚Ø¯ÙŠÙ… Ø¥Ù„Ù‰ Ø±Ø§Ø¨Ø· API
  static convertToApiUrl(oldUrl: string, req: Request): string {
