export function addCacheVersion(url: string, type: keyof typeof CACHE_VERSIONS = 'images'): string {
  if (!url || url.includes('?v=') || url.includes('&v=')) {
    return url; // Ø§Ù„Ø±Ø§Ø¨Ø· ÙŠØ­ØªÙˆÙŠ Ø¨Ø§Ù„ÙØ¹Ù„ Ø¹Ù„Ù‰ Ø¥ØµØ¯Ø§Ø± Ø£Ùˆ ÙØ§Ø±Øº
  }

