// Ø¯Ø§Ù„Ø© Ù„Ù„ØªØ­Ù‚Ù‚ Ù…Ù† ØµØ­Ø© Ø§Ù„Ø¥ØµØ¯Ø§Ø±
export function isValidCacheVersion(url: string, type: keyof typeof CACHE_VERSIONS = 'images'): boolean {
